package com.segmentloop.controller.segment;

import com.segmentloop.model.segment.Segment;
import com.segmentloop.model.segment.NoteEntry;
import com.segmentloop.service.segment.SegmentService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.net.URLDecoder;
import java.nio.charset.StandardCharsets;
import java.util.List;
import java.util.Map;
import java.util.Optional;

@RestController
@RequestMapping("/segment/api")
@CrossOrigin(origins = {"http://localhost:3000", "http://localhost:8080", "http://localhost:5173"})
public class SegmentController {

    @Autowired
    private SegmentService segmentService;

    @GetMapping("/segments")
    public ResponseEntity<List<Segment>> getAllSegments(@RequestParam(required = false) String source_id) {
        List<Segment> segments;

        if (source_id != null) {
            try {
                Integer sourceId = Integer.parseInt(source_id);
                segments = segmentService.getSegmentsBySourceId(sourceId);
            } catch (NumberFormatException e) {
                return ResponseEntity.badRequest().build();
            }
        } else {
            segments = segmentService.getAllSegments();
        }

        return ResponseEntity.ok(segments);
    }

    @GetMapping("/segments/{segmentPath:.+}")
    public ResponseEntity<Segment> getSegment(@PathVariable String segmentPath) {
        try {
            String decodedPath = URLDecoder.decode(segmentPath, StandardCharsets.UTF_8);
            decodedPath = decodedPath.replace("%2F", "/");

            Optional<Segment> segment = segmentService.getSegmentByFilepath(decodedPath);
            return segment.map(ResponseEntity::ok)
                         .orElse(ResponseEntity.notFound().build());
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @PostMapping("/segments")
    public ResponseEntity<Segment> createSegment(@RequestBody Segment segment) {
        try {
            Segment createdSegment = segmentService.createSegment(segment);
            return ResponseEntity.ok(createdSegment);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @PutMapping("/segments/{segmentPath:.+}")
    public ResponseEntity<Segment> updateSegment(@PathVariable String segmentPath, @RequestBody Segment segmentUpdate) {
        try {
            String decodedPath = URLDecoder.decode(segmentPath, StandardCharsets.UTF_8);
            decodedPath = decodedPath.replace("%2F", "/");

            Segment updatedSegment = segmentService.updateSegmentByFilepath(decodedPath, segmentUpdate);
            return ResponseEntity.ok(updatedSegment);
        } catch (RuntimeException e) {
            return ResponseEntity.notFound().build();
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @PostMapping("/segments/update")
    public ResponseEntity<Segment> updateSegmentSimple(@RequestBody Map<String, Object> updateData) {
        try {
            String filepath = (String) updateData.get("filepath");
            String segmentId = (String) updateData.get("_id");

            if (filepath == null && segmentId == null) {
                return ResponseEntity.badRequest().build();
            }

            Segment segmentUpdate = new Segment();

            if (updateData.containsKey("play_count")) {
                segmentUpdate.setPlayCount((Integer) updateData.get("play_count"));
            }
            if (updateData.containsKey("last_played")) {
                // Handle datetime conversion if needed
                segmentUpdate.setLastPlayed(null); // TODO: Parse datetime
            }
            if (updateData.containsKey("note")) {
                segmentUpdate.setNote((String) updateData.get("note"));
            }
            if (updateData.containsKey("starred")) {
                segmentUpdate.setStarred((Boolean) updateData.get("starred"));
            }

            Segment updatedSegment;
            if (filepath != null) {
                updatedSegment = segmentService.updateSegmentByFilepath(filepath, segmentUpdate);
            } else {
                Optional<Segment> existingSegment = segmentService.getSegmentById(segmentId);
                if (existingSegment.isPresent()) {
                    Segment segment = existingSegment.get();
                    // Apply updates
                    if (segmentUpdate.getPlayCount() != null) segment.setPlayCount(segmentUpdate.getPlayCount());
                    if (segmentUpdate.getNote() != null) segment.setNote(segmentUpdate.getNote());
                    if (segmentUpdate.getStarred() != null) segment.setStarred(segmentUpdate.getStarred());

                    updatedSegment = segmentService.updateSegment(segment);
                } else {
                    return ResponseEntity.notFound().build();
                }
            }

            return ResponseEntity.ok(updatedSegment);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @GetMapping("/segments/{segmentPath:.+}/notes")
    public ResponseEntity<List<NoteEntry>> getSegmentNotes(@PathVariable String segmentPath) {
        try {
            String decodedPath = URLDecoder.decode(segmentPath, StandardCharsets.UTF_8);
            decodedPath = decodedPath.replace("%2F", "/");

            Optional<Segment> segment = segmentService.getSegmentByFilepath(decodedPath);
            return segment.map(s -> ResponseEntity.ok(s.getNotes()))
                         .orElse(ResponseEntity.notFound().build());
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @DeleteMapping("/segments/{id}")
    public ResponseEntity<Void> deleteSegment(@PathVariable String id) {
        try {
            segmentService.deleteSegment(id);
            return ResponseEntity.noContent().build();
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }
}

